mapper.update:
UPDATE exercise_video
SET view_cnt = view_cnt + 1
WHERE id = #{id};

mapper.select:
  	SELECT ev.id AS id, ev.title AS title, ev.url AS url, ev.view_cnt AS view_cnt, ev.review_cnt AS review_cnt, ev.reg_date AS video_reg_date, evl.reg_date AS like_reg_date
FROM exercise_video_like evl
JOIN exercise_video ev ON evl.exercise_video_id = ev.id
WHERE evl.member_id = #{memberId} 
ORDER BY evl.reg_date DESC;

mapper.update:
UPDATE exercise_video
SET deleted = true
WHERE id = #{id};
